<script>
import Datepicker from 'vue2-datepicker';
import 'vue2-datepicker/index.css';

export default {
    components: {
        Datepicker,
    },
    data() {
        return {
            pickupDate: '',
            dropOffDate: '',
            pickupAdresse: 'All Locations',
            selectedCarBrands: 'All Brands',
            // banner: this.$settings.sections.banner,
            // form: this.$settings.sections.form,
            products: [],
            brands: [],
            selectedTitle: "Véhicle",
            slideWidth: 0,
            offsetX: 0,
            startX: 0,
            isDragging: false,
            imageWidth: 80, // Set your desired image width
            imageHeight: 80, // Set your desired image height

        };
    }, async fetch() {

        try {
            const { data } = await this.$storeino.brands.search()
            this.brands = data.results;

            const response = await this.$storeino.products.search({ productType: 'BOOKING' });
            this.products = response.data.results;

        } catch (err) {
            console.error(err);
            this.$nuxt.error({ statusCode: 404, message: 'product_not_found' });
        }
    },

    mounted() {
        // this.$nextTick(() => {
        //     // Calculate slide width
        //     this.slideWidth = this.$refs.carouselItem.offsetWidth;
        //     // Automatically slide every 6 seconds
        //     setInterval(this.autoSlide, 6000);
        // });
    },
    computed: {



        isHomePage() {
            return this.$route.path === '/';
        },


        uniqueCities() {

            const citiesSet = new Set();

            this.products.forEach((item) => {
                if (item.bookingProps.firstAddresses && item.bookingProps.firstAddresses.length > 0) {
                    item.bookingProps.firstAddresses.forEach((address) => {
                        const city = address.city.name;
                        if (!citiesSet.has(city)) {
                            citiesSet.add(city);
                        }
                    });
                }
            });

            return Array.from(citiesSet);
        },
    },
    methods: {

        startDrag(event) {
            this.isDragging = true;
            this.startX = event.clientX;
        },
        dragging(event) {
            if (this.isDragging) {
                const deltaX = event.clientX - this.startX;
                this.offsetX += deltaX;
                this.startX = event.clientX;
            }
        },
        endDrag() {
            this.isDragging = false;
        },
        autoSlide() {
            if (!this.isDragging) {
                if (this.offsetX > -this.slideWidth * (this.brands.length - 6)) { // Show 6 items per slide
                    this.offsetX -= this.slideWidth;
                } else {
                    this.offsetX = 0;
                }
            }
        },
        goToBrand(brand) {
            // Handle clicking on brand
            console.log('Clicked on brand:', brand);
        },

        changeTitle(title) {
            this.selectedTitle = title;
        },

        searchItems() {
            const formattedPickupDate = this.pickupDate instanceof Date ? this.pickupDate.toISOString() : '';
            const formattedDropOffDate = this.dropOffDate instanceof Date ? this.dropOffDate.toISOString() : '';

            const queryParams = {
                brands: this.selectedCarBrands.toLowerCase(),
                pickupDate: formattedPickupDate,
                dropOffDate: formattedDropOffDate,
            };

            if (this.pickupAdresse !== 'All Locations') {
                queryParams.pickupAdresse = this.pickupAdresse;
            }


            const url = `/cars?${new URLSearchParams(queryParams)}`;

            this.$router.push(url);
        },
    },
};
</script>

